let%expect_test "long input list" =
  let%bind () = test "variable-input" [ "32769" ] [] in
  [%expect
    {|
      a
      a
      a
      a
      a
      a
      a
    > a
      32769/32769
    >
    |}];
  return ()
;;

let%expect_test "large input chosen" =
  let%bind () = test "variable-input" [ "2 -option-length 170000" ] [ Enter ] in
  [%expect
    {|
    
    
    
    
    
    
    
    
    )
    bash$
    |}];
  (* At one point choosing entries that caused a sufficiently long output would cause
     [Fzf] to never return. In this test we see the truncated end of the chosen output and
     the 'bash$' indicates that [Fzf] returned and finished as expected. *)
  return ()
;;
